mapper.select:
SELECT ano, aid, aname, aregdate, admin_product, admin_order, admin_members, admin_board, admin_add, admin_account, admin_auth
FROM admin 

ORDER BY ${orderby} ${direct}
LIMIT ${startIdx}, ${pageRow}

mapper.delete:
DELETE FROM admin
WHERE ano IN

${ano}

mapper.select:
SELECT ano, aid, aname, aregdate, admin_product, admin_order, admin_members, admin_board, admin_add, admin_account, admin_auth
FROM admin 
WHERE ano = ${ano}

mapper.update:
UPDATE admin SET
aname=#{aname}, admin_product=${admin_product}, admin_order=${admin_order}, admin_members=${admin_members}, admin_board=${admin_board}, admin_add=${admin_add}, admin_account=${admin_account}, admin_auth=${admin_auth}
, apwd=MD5(#{apwd})
WHERE ano=${ano}
