mapper.select:
SELECT *,
	(SELECT COUNT(*) FROM comment WHERE cm_board_no=notice.nno) AS comment_count 
FROM notice

ORDER BY ${orderby} ${direct}
LIMIT ${startIdx},${pageRow}

mapper.update:
UPDATE notice SET
	ntitle=#{ntitle}, ncontent=#{ncontent}
	
		, nfile_org=#{nfile_org}, nfile_real=#{nfile_real}
	
WHERE nno=#{nno}

UPDATE notice SET
	nfile_org='', nfile_real=''
WHERE nno=#{nno}
