mapper.select:
SELECT * FROM product


	ORDER BY (select AVG(review.rstar) FROM review)


	ORDER BY ${orderby}

${direct}
LIMIT ${startIdx}, ${pageRow}

mapper.update:
UPDATE product SET
pno=#{pno}, pprice=#{pprice}, pname=#{pname}, pcount=#{pcount}, pregdate=NOW()

	, pimg1_org=#{pimg1_org}, pimg1_real=#{pimg1_real}


	, pimg2_org=#{pimg2_org}, pimg2_real=#{pimg2_real}


	, pimg3_org=#{pimg3_org}, pimg3_real=#{pimg3_real}

WHERE pno=#{pno}

UPDATE product SET
pimg1_org="", pimg1_real="", pimg2_org="", pimg2_real="", pimg3_org="", pimg3_real=""
WHERE pno=#{pno}
